var p=[];
p.push( '--retry', '0' );
p.push( '-k' );

//***** Proxy Setting *****
if (global.get("EnableProxy")){
    p.push( '--proxy' );
    p.push( global.get("ProxyServer"));
}
//**************************

for (var k in msg.headers ) {
    p.push( "-H", "\"" + k + ": " + msg.headers[k] + "\"" );
}
p.push( msg.url );
var v = JSON.stringify( msg.payload );
p.push( '-d', v );
msg.payload=p.join(" ");
return msg;

var buffer = flow.get("UPLINK-BUFFER");
var conStatus = flow.get("CON-STATUS");
var isNewMsg = false;

// detect new message
if(msg.url !== undefined){
    isNewMsg = true;
    if(buffer === undefined){
        buffer = [];
    }
    buffer.push(JSON.parse(JSON.stringify(msg)));
    // take always the first message
    if(buffer.length > 1){
        msg = JSON.parse(JSON.stringify(buffer[0]));
    }
// detect RC
} else if(msg.payload.code !== undefined) {
    if(msg.payload.code === 0){
        conStatus = true;
        buffer.shift();
        // take the next message if buffer is not emty
        if(buffer.length > 0){
            isNewMsg = true;
            msg = JSON.parse(JSON.stringify(buffer[0]));
        }
    } else {
        conStatus = false;
        if(buffer.length > 500){
            buffer.shift();
        }
    }
} else if(msg.deleteBuffer !== undefined) {
    buffer = [];
} else {
    return;
}

flow.set("UPLINK-BUFFER", buffer);
flow.set("CON-STATUS", conStatus);
var msg1 = { "payload":conStatus };
var msg2 = { "payload":buffer.length };

if(isNewMsg){
    return [, , msg];
}else {
    return [msg1, msg2, ];
}

if(msg.payload.code === 0){
    node.status({fill:"green",shape:"dot",text:"Connected"});
}else if(msg.payload.code === 5){
    node.status({fill:"red",shape:"ring",text:"Error RC:" + msg.payload.code + " (wrong proxy)"});
}else {
    node.status({fill:"red",shape:"ring",text:"Error RC:" + msg.payload.code});
}
return msg;

if(msg.Server === undefined){
    node.status({fill:"red",shape:"dot",text:"msg.Server is missing in the flow"});
    return;
} else if(msg.CustomHeader2 === undefined){
    node.status({fill:"red",shape:"dot",text:"msg.CustomHeader2 is missing in the flow"});
    return;
} else if(msg.prefix === undefined){
    node.status({fill:"red",shape:"dot",text:"msg.prefix is missing in the flow"});
    return;
} else if(msg.proxy === undefined){
    node.status({fill:"red",shape:"dot",text:"msg.proxy is missing in the flow"});
    return;
} else if(msg.Server === "" || msg.CustomHeader2 === "" || 
            msg.Server === "https://cnm-ih-na.azure-devices.net/devices/xxx" || 
            msg.Server === "enter here" || 
            msg.CustomHeader2 === "Authorization;SharedAccessSignature sr=cnm-ih-na.azure-devices.netxxx" ||
            msg.CustomHeader2 === "enter here") {
    node.status({fill:"red",shape:"dot",text:"Please set Monitor Data"});
    return;
}
msg.method = "POST";
msg.url = msg.Server;
msg.headers = {
    "Authorization": msg.CustomHeader2,
	"Content-Type": "application/json"
};
msg.headers.Authorization=msg.headers.Authorization.replace(/^.*SharedAccessSignature/,"SharedAccessSignature");
msg.send_body=msg.payload;
//node.status({fill:"green",shape:"dot",text:"OK"});
return msg;

var p=[];
p.push( '--retry', '0' );
p.push( '-k' );

//***** Proxy Setting *****
if(msg.proxy !== undefined && msg.proxy.startsWith("http")){
    p.push( '--proxy' );
    p.push( msg.proxy );
}
//**************************

for (var k in msg.headers ) {
    p.push( "-H", "\"" + k + ": " + msg.headers[k] + "\"" );
}
p.push( msg.url );
var v = JSON.stringify( msg.payload );
p.push( '-d', v );
msg.payload=p.join(" ");
return msg;

var buffer = flow.get("UPLINK-BUFFER");
var conStatus = flow.get("CON-STATUS");
var msgInTube = flow.get("MSG-IN-TUBE");
var isNewMsg = false;

if ((typeof msgInTube) !== "boolean") {
    msgInTube = false;
}

if(msg.deleteBuffer !== undefined) {    // delete Buffer
    buffer = [];
    isNewMsg = false;
    msgInTube = false;
} else if(msg.payload.code !== undefined) { // detect RC
    if(msg.payload.code === 0){
        conStatus = true;
        msgInTube = false;
        buffer.shift();
        // take the next message if buffer is not emty
        if(buffer.length > 0){
            isNewMsg = true;
            msgInTube = true;
            msg = JSON.parse(JSON.stringify(buffer[0]));
        }
    } else {
        conStatus = false;
        msgInTube = false;
        if(buffer.length > 500){
            buffer.shift();
        }
    }
} else if(msg.url !== undefined){   // detect new message
    if(buffer === undefined){
        buffer = [];
    }
    buffer.push(JSON.parse(JSON.stringify(msg)));
    // take always the first message
    if(msgInTube === false){
        isNewMsg = true;
        msgInTube = true;
        msg = JSON.parse(JSON.stringify(buffer[0]));
    } else {
        if((msg.timestampEpoc) > buffer[0].timestampEpoc + 1000){
            isNewMsg = true;
        }
    }
} else {
    return;
}

flow.set("UPLINK-BUFFER", buffer);
flow.set("CON-STATUS", conStatus);
flow.set("MSG-IN-TUBE", msgInTube);
node.status({fill:"yellow",shape:"dot",text:"Buffer Size: " + buffer.length});

if(isNewMsg){
    return msg;
}else {
    return;
}

// Applies a simple RC low pass filter to incoming payload values
var tc = 30*1000;       // time constant in milliseconds

var lastValue = context.get('lastValue');
if (typeof lastValue == "undefined") lastValue = msg.payload;
var lastTime = context.get('lastTime') || null;
var now = new Date();
var currentValue = msg.payload;
if (lastTime === null) {
    // first time through
    newValue = currentValue;
} else {
    var dt = now - lastTime;
    var newValue;

    if (dt > 0) {
        var dtotc = dt / tc;
        newValue = lastValue * (1 - dtotc) + currentValue * dtotc;
    } else {
        // no time has elapsed leave output the same as last time
        newValue = lastValue;
    }
}
context.set('lastValue', newValue);
context.set('lastTime', now);

msg.payload = newValue + 20;
return msg;

// Applies a simple RC low pass filter to incoming payload values
var tc = 10*1000;       // time constant in milliseconds

var lastValue = context.get('lastValue');
if (typeof lastValue == "undefined") lastValue = msg.payload;
var lastTime = context.get('lastTime') || null;
var now = new Date();
var currentValue = msg.payload;
if (lastTime === null) {
    // first time through
    newValue = currentValue;
} else {
    var dt = now - lastTime;
    var newValue;

    if (dt > 0) {
        var dtotc = dt / tc;
        newValue = lastValue * (1 - dtotc) + currentValue * dtotc;
    } else {
        // no time has elapsed leave output the same as last time
        newValue = lastValue;
    }
}
context.set('lastValue', newValue);
context.set('lastTime', now);

msg.payload = newValue;
return msg;

// stores messages in a fifo until the specified number have been received, 
// then releases them as new messages are received.
// during the filling phase the earliest message is passed on each time 
// a message is received, but it is also left in the fifo
var fifoMaxLength = 2;
var fifo = context.get('fifo') || [];
// push the new message onto the top of the array, messages are shifted down and
// drop off the front
var length = fifo.push(msg);  // returns new length
if (length > fifoMaxLength) {
    newMsg = fifo.shift();
} else {
    // not full yet, make a copy of the msg and pass it on
    var newMsg = JSON.parse(JSON.stringify(fifo[0]));
}
context.set('fifo', fifo);
return newMsg;

