var buffer = flow.get("UPLINK-BUFFER");
var conStatus = flow.get("CON-STATUS");
var msgInTube = flow.get("MSG-IN-TUBE");
var isNewMsg = false;

if ((typeof msgInTube) !== "boolean") {
    msgInTube = false;
}

if(msg.deleteBuffer !== undefined) {    // delete Buffer
    buffer = [];
    isNewMsg = false;
    msgInTube = false;
} else if(msg.payload.code !== undefined) { // detect RC
    if(msg.payload.code === 0){
        conStatus = true;
        msgInTube = false;
        buffer.shift();
        // take the next message if buffer is not emty
        if(buffer.length > 0){
            isNewMsg = true;
            msgInTube = true;
            msg = JSON.parse(JSON.stringify(buffer[0]));
        }
    } else {
        conStatus = false;
        msgInTube = false;
        if(buffer.length > 500){
            buffer.shift();
        }
    }
} else if(msg.url !== undefined){   // detect new message
    if(buffer === undefined){
        buffer = [];
    }
    buffer.push(JSON.parse(JSON.stringify(msg)));
    // take always the first message
    if(msgInTube === false){
        isNewMsg = true;
        msgInTube = true;
        msg = JSON.parse(JSON.stringify(buffer[0]));
    } else {
        if((msg.timestampEpoc) > buffer[0].timestampEpoc + 1000){
            isNewMsg = true;
        }
    }
} else {
    return;
}

