// stores messages in a fifo until the specified number have been received, 
// then releases them as new messages are received.
// during the filling phase the earliest message is passed on each time 
// a message is received, but it is also left in the fifo
var fifoMaxLength = 2;
var fifo = context.get('fifo') || [];
// push the new message onto the top of the array, messages are shifted down and
// drop off the front
var length = fifo.push(msg);  // returns new length
if (length > fifoMaxLength) {
    newMsg = fifo.shift();
} else {
    // not full yet, make a copy of the msg and pass it on
    var newMsg = JSON.parse(JSON.stringify(fifo[0]));
}
context.set('fifo', fifo);
return newMsg;

